import { Col, Input, Row, Space, DatePicker, Button } from "antd";
import React, { useCallback, useState } from "react";
import { useDispatch } from "react-redux";
import { AIRPORT_SEARCH_REQUEST } from "../reducers/search";
import SearchInput from "./SearchInput";

const { RangePicker } = DatePicker;

function SearchSub() {
  const [departureText, setDepartureText] = useState("");
  const [isDepartureText, setIsDepartureText] = useState(false);
  const dispatch = useDispatch();

  const onChangeDeparture = useCallback(
    (e) => {
      setDepartureText(e.target.value);
      dispatch({
        type: AIRPORT_SEARCH_REQUEST,
        data: e.target.value,
      });
    },
    [departureText]
  );

  return (
    <div
      style={{
        background: "black",
        width: "100%",
        margin: "0 auto",
        maxWidth: "1096px",
        height: "220px",
        padding: "1.5em",
      }}
    >
      <Row
        style={{
          width: "20rem",
          margin: "auto",
          width: "100%",
          maxWidth: "900px",
        }}
      >
        <Col
          xs={{ span: 24 }}
          md={{ span: 6 }}
          style={{ position: "relative" }}
        >
          <SearchInput />
        </Col>
        <Col xs={{ span: 24 }} md={{ span: 6 }}>
          <Input />
        </Col>
        <Col xs={{ span: 24 }} md={{ span: 7 }}>
          <RangePicker style={{ width: "100%" }} />
        </Col>
        <Col xs={{ span: 24 }} md={{ span: 5 }}>
          <Input />
        </Col>
        <Row
          justify="end"
          style={{
            width: "20rem",
            width: "100%",
            maxWidth: "900px",
            marginTop: "10px",
          }}
        >
          <Col xs={{ span: 24 }} md={{ span: 5 }}>
            <Button style={{ width: "100%" }}>검색</Button>
          </Col>
        </Row>
      </Row>
    </div>
  );
}
export default SearchSub;
