import { Input } from "antd";
import React, { useCallback, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { AIRPORT_SEARCH_REQUEST } from "../reducers/search";

function SearchInput() {
  const [departureText, setDepartureText] = useState("");
  const [isDepartureText, setIsDepartureText] = useState(false);
  const { searchList } = useSelector((state) => state.search);
  const dispatch = useDispatch();

  const onChangeDeparture = useCallback(
    (e) => {
      setDepartureText(e.target.value);
      dispatch({
        type: AIRPORT_SEARCH_REQUEST,
        data: e.target.value,
      });
    },
    [departureText]
  );
  return (
    <>
      <Input
        placeholder="도시명"
        onChange={onChangeDeparture}
        value={departureText}
      />
      <div
        style={{
          position: "absolute",
          width: "100%",
          background: "white",
        }}
      >
        {searchList.map((v) => (
          <>
            <div style={{ textAlign: "left", marginTop: "5px" }}>
              {v.PlaceName}({v.PlaceId})
            </div>
          </>
        ))}
      </div>
    </>
  );
}

export default SearchInput;
